mapper.select:
select id, coursename, major_id as majorId, admin_id as adminId
from course
where course.id=#{courseId}

select id, coursename, major_id as majorId, admin_id as adminId
from course
where course.coursename=#{courseName}

mapper.update:
update course

    coursename = #{courseName} ,
    major_id = #{majorId} ,
    admin_id = #{adminId} 

where id = #{id}
