mapper.select:
select *
from role, user_role
where role.id=user_role.rid and user_role.uid=#{id};

mapper.update:
update user

    username = #{username} ,
    email = #{email} ,
    admin_id = #{adminId} ,
    password = #{password} 

where id=#{id};

mapper.select:
select id as id, username as username, email as email, admin_id as adminId
from user
where admin_id=#{adminId}

select user.id as id, username as username
from user join course_user on user.id = course_user.user_id
where course_user.course_id = #{courseId}
